


var IndexesDAO = module.exports.IndexesDAO = function (db)
{
	this.db = db;
};

/**
 * IndexesDAO.createIndex
 *
 * @param collectionName
 * @param index
 * @param options
 * 	{sparse: true} to include only records that have indexed field set
 * 	{unique: true} for unique indexes
 * 	If the options is a boolean value, then it indicates if it's an unique index or not
 * @param callback
 */
IndexesDAO.prototype.createIndex = function (collectionName, index, options, callback)
{
	this.db.collection(collectionName).ensureIndex(index, options, function (err,indexName)
	{
		callback(err);
	});
};

IndexesDAO.prototype.dropIndexes = function (collectionName,callback)
{
	this.db.collection(collectionName).dropIndexes(function (err,success)
	{
		callback(err);
	});
};