var ListDAO = module.exports.ListDAO = function (db)
{
	this.db = db;
};

ListDAO.prototype.findListById = function (id, callback)
{
	this.db.collection("v1_list").find({_id:id},{limit: 1}).toArray(function (err, items)
	{
		callback(items.length > 0 ? items[0] : null);
	});
};

ListDAO.prototype.findAllLists = function (callback)
{
	this.db.collection("v1_list").find().toArray(function (err, items)
	{
		callback(items);
	});
};