module.exports.findAllLists = function (db,callback)
{
	db.collection("v1_list").find().toArray(function (err,items) {
		callback(items);
	});
};

module.exports.findListById = function (db,id,callback)
{
	db.collection("v1_list").find({"_id": id}).toArray(function (err,items) {
		callback(items[0].value);
	});
};