
var ListDAO = module.exports.ListDAO = function (db)
{
	var self = this;

	this.db = db;

	this.findAllLists = function (callback)
	{
		self.db.collection("v1_list").find().toArray(function (err,items)
		{
			callback(items);
		});
	};

	this.findListById = function (id,callback)
	{
		db.collection("v1_list").find({"_id": id}).toArray(function (err,items)
		{
			callback(items[0].value);
		});
	};
};