
var mongo = require("mongodb");
var util = require('util');

module.exports.findActivityForUserById = function(db,id,callback)
{
	var userId = new mongo.ObjectID(id);

	db.collection("v1_user_activity").find({userId: userId},{limit: 100}).toArray(function (err,items) {
		callback(items);
	});
};

module.exports.createActivityForUserById = function(db,id,activity,callback)
{
	activity.userId = new mongo.ObjectID(id);

	db.collection("v1_user_activity").insert(activity, function (err,items)
	{
		callback(activity);
	});
}