
var listly = require('listly');
var json = require('json');

var ListRemoteDAO = module.exports.ListRemoteDAO = function ()
{
	listly.RemoteDAO.apply(this, arguments);
};

listly.utils.inherit(ListRemoteDAO, listly.RemoteDAO);

ListRemoteDAO.prototype.findAllLists = function (callback)
{
	this.get('/lists',null,function (err,resp,body)
	{
		callback(err,body);
	});
};

ListRemoteDAO.prototype.findListById = function (listId, callback)
{
	this.get('/lists/' + listId,null,function (err,resp,body)
	{
		callback(err,body);
	});
};