
var listly = require('listly');

var LoginRemoteDAO = module.exports.LoginRemoteDAO = function ()
{
	listly.RemoteDAO.apply(this, arguments);
};

listly.utils.inherit(LoginRemoteDAO, listly.RemoteDAO);


LoginRemoteDAO.prototype.loginUser = function (username, password, callback)
{
	var data = {username: username, password: password};

	this.post('/login',data,null,function (err,resp,body)
	{
		callback(resp.statusCode,body);
	});
};

