
var listly = require('listly');
var json = require('json');

var SubmissionRemoteDAO = module.exports.SubmissionRemoteDAO = function ()
{
	listly.RemoteDAO.apply(this, arguments);
};

listly.utils.inherit(SubmissionRemoteDAO, listly.RemoteDAO);

SubmissionRemoteDAO.prototype.findAllSubmissions = function (callback)
{
	this.get('/submissions',null,function (err,resp,body)
	{
		callback(err,body);
	});
};

SubmissionRemoteDAO.prototype.findSubmissionById = function (submissionId, callback)
{
	this.get('/submissions/' + submissionId,null,function (err,resp,body)
	{
		callback(err,body);
	});
};

SubmissionRemoteDAO.prototype.createSubmission = function (submission, callback)
{
	this.post('/submissions/',submission,null,function (err,resp,body)
	{
		callback(err,body);
	});
};