
var listly = require('listly');
var json = require('json');

var UserRemoteDAO = module.exports.UserRemoteDAO = function ()
{
	listly.RemoteDAO.apply(this, arguments);
};

listly.utils.inherit(UserRemoteDAO, listly.RemoteDAO);

UserRemoteDAO.prototype.findUserByUsername = function (username, callback)
{
	this.get('/users/' + username,null,function (err,resp,body)
	{
		callback(err,json.parse(body));
	});
};

UserRemoteDAO.prototype.updateUser = function (username, user, callback)
{
	this.put('/users/' + username,user,null,function (err,resp,body)
	{

	});
};