



var ListlyDBManager = module.exports.ListlyDBManager = function (listlyDataServer)
{
	this.listlyDataServer = listlyDataServer;
	this.indexesDAO = listlyDataServer.indexesDAO;
};

ListlyDBManager.prototype.ensureIndexes = function ()
{
	var self = this;

	this.indexesDAO.createIndex("v1_user", {username: 1}, true, function (err)
	{
		if (err !== undefined)
		{
			self.listlyDataServer.logError(err);
		}
	});
};