
var dao = require('listly').userDAO;

exports.doLogin = function (db) {

	return function(req, res)
	{
		var login = {
			username: req.body.username,
			password: req.body.password
		};

		dao.findUserByLogin(db,login,function(loginSuccessful)
		{
			if (loginSuccessful)
			{
				req.session.username = req.body.username;
				res.writeHead(202);
				res.end('login successful');
			}
			else
			{
				res.writeHead(207);
				res.end('login failed, please try again');
			}
		});
	};
};

exports.doRegister = function (db) {

	return function (req,res)
	{
		var user = {
			username: req.body.username,
			password: req.body.password
		};

		dao.createUser(db, user, function(registerMsg)
		{
			if (registerMsg == true)
			{
				req.session.username = req.body.username;
				res.writeHead(202);
				res.end('registration successful');
			}
			else
			{
				res.writeHead(207);
				res.end(registerMsg);
			}
		});
	};
};