
var listly = require('listly');

var UserRoutes = module.exports.UserRoutes = function (listlyServer)
{

};

UserRoutes.prototype.index = function(req,res)
{
	res.render('user/index', listly.renderUtils.params(req,{title: "Profile"}));
};

UserRoutes.prototype.edit = function(req, res)
{
	res.render('user/edit', listly.renderUtils.params(req,{layout: false}));
};

UserRoutes.prototype.view = function(req, res)
{
	res.render('user/view', listly.renderUtils.params(req,{layout: false}));
};

UserRoutes.prototype.home = function(req, res)
{
	res.render('user/home/index', listly.renderUtils.params(req,{title: "Home"}));
};

UserRoutes.prototype.homeMain = function(req, res)
{
	res.render('user/home/main', listly.renderUtils.params(req,{layout: false}));
};

UserRoutes.prototype.profile = function (req,res)
{
	res.render('user/profile/index', listly.renderUtils.params(req,{title: "Profile"}));
};