
var AmazonSES = require('amazon-ses');

var ListlyAmazonSES = module.exports.ListlyAmazonSES = function ()
{
	var self = this;

	this.ses = new AmazonSES('AKIAIWOE3KKIF6XDEOSA', 'cFQSf59sAr3Qcxt15RsUnA5HRYmiI6eY+QT8Re22');
};

ListlyAmazonSES.prototype.sendMessage = function (params)
{
	this.ses.send({
		from: params.from
		, to: params.to
		, replyTo: params.replyTo
		, subject: params.subject
		, body: {
			text: params.text
			, html: params.html
		}
	});
};