
var AmazonSES = require('amazon-ses');

module.exports.createEmailService = function ()
{
	return new ListlyEmailService();
};

var ListlyEmailService = function ()
{
	var self = this;

	this.ses = new AmazonSES('AKIAIWOE3KKIF6XDEOSA', 'cFQSf59sAr3Qcxt15RsUnA5HRYmiI6eY+QT8Re22');
};

ListlyEmailService.prototype.sendMessage = function (to,subject,text)
{
	this.ses.send({
		from: 'ariel@deskmasters.com'
		, to: [to]
		, replyTo: ['ariel@deskmasters.com']
		, subject: subject
		, body: {
			text: text
			, html: text
		}
	});
};