

module.exports.configure = function (everyauth)
{
	everyauth.password
		.getLoginPath('/login')
		.postLoginPath('/doLogin')
		.loginView('login/index')
		.loginFormFieldName('username')
		.authenticate( function (login, password)
		{
			return login == "arieljake" && password == "test";
		})
		.loginSuccessRedirect('/')
		.getRegisterPath('/register')
		.postRegisterPath('/doRegister')
		.registerView('login/register')
		.validateRegistration( function (newUserAttributes)
		{
			return null;
		})
		.registerUser( function (newUserAttributes)
		{
			return {};
		})
		.registerSuccessRedirect('/');

	everyauth.debug = true;
};