module.exports.createRouter = function (routes)
{
	return new ListlyRouter(routes);
};

var ListlyRouter = function (routes)
{
	var self = this;

	this.routes = routes;

	this.init = function (serverParts)
	{
		for (var i = 0; i < self.routes.length; i++)
		{
			var route = self.routes[i];

			route.init(serverParts);
		}
	}
};