
var connectMongo = require('connect-mongo');

module.exports.createSessionSettings = function (express)
{
	var mongoStore = connectMongo(express);

	return {
		secret: "listlyisfun",
		store: new mongoStore({
								  db: "listly",
								  collection: "v1_session",
								  host: "127.0.0.1",
								  port: 27017,
								  auto_reconnect: true,
								  clear_interval: 300,
								  stringify: true
							  })
	};
}