var dao = require('listly').listDAO;

exports.getAllLists = function (db)
{
	return function (req,res){
		dao.findAllLists(db,function(items)
		{
			res.send(items);
		});
	};
};

exports.getListById = function (db,idFxn)
{
	return function (req,res){
		dao.findListById(db,idFxn(req),function(item)
		{
			res.send(item);
		});
	};
};